// // SLIDER
function mainSlider1Init() {
	var interleaveOffset = 0.5;
	var swiperOptions = {
		loop: true,
		speed: 1000,
		parallax: true,
		// autoplay: {
		//     delay: 3500,
		//     disableOnInteraction: false,
		// },
		autoplay: false,
		grabCursor: false,
		pagination: {
			el: '.swiper-pagination',
			clickable: true
			// type: "fraction",
		},
		navigation: {
			nextEl: '.swiper-button-next',
			prevEl: '.swiper-button-next'
		}
		
	};

	var homeSlider = new Swiper('.homeSlider', swiperOptions);

	var pageSection = $('.bg-image');
	pageSection.each(function (indx) {
		if ($(this).attr('data-background')) {
			$(this).css('background-image', 'url(' + $(this).data('background') + ')');
		}
	});
}
mainSlider1Init();



//gsap loader
const tl = gsap.timeline({ duration: 0.2 });
function preLoader(tl) {
	tl.to('.preLoader.black > img', {
		delay: 1,
		y: 50,
		autoAlpha: 0
	})
		.to('.preLoader.black', {
			yPercent: -100
		})
		.to('.preLoader.white', {
			yPercent: -100
		})
		.to('.preLoader', {
			css: {
				display: 'none'
			}
		})

		.from('.top-nav .social-links a', {
			x: -50,
			autoAlpha: 0
		})
		.from(
			'.top-nav .search-box',
			{
				autoAlpha: 0,
				x: 100,
				stagger: 0.05
			},
			'>-0.5'
		)

		.from('.navbar-brand > img', {
			x: -50,
			autoAlpha: 0
		})

		.from(
			'.navbar-nav > li, .form-inline a',
			{
				autoAlpha: 0,
				x: 100,
				stagger: 0.02
			},
			'>-0.2'
		)

		.from('.slideOne', {
			autoAlpha: 0,
			yPercent: 100,
			stagger: 0.05
		})
		.from('.slideTwo', {
			autoAlpha: 0,
			yPercent: -100,
			stagger: 0.05
		}).from('.slideThree', {
			autoAlpha: 0,
			xPercent: -100,
			stagger: 0.05
		});
	// .from(
	//  '.bg-image',
	//  {
	//      autoAlpha: 0
	//  },
	//  '<-0.25'
	// )
}

// function initialization
$(window).on('load', function () {
	preLoader(tl);
});

